let series_moving_avg_fl = (y_series:dynamic, n:int, center:bool=false)
{
    series_fir(y_series, repeat(1, n), true, center)
};
requests
| extend totalTokens = todouble(parse_json(tostring(parse_json(tostring(parse_json(tostring(customDimensions.["Response-Body"])).usage)).total_tokens)))
| summarize sumTotalTokens=sum(totalTokens) by bin(timestamp, 1h)
| extend dummy = 1 // Dummy column to align time series
| make-series sumTotalTokens_series=sum(sumTotalTokens) on timestamp step 1h by dummy
| extend sumTotalTokens_moving_avg = series_moving_avg_fl(sumTotalTokens_series, 12, true)
| mv-expand timestamp to typeof(datetime), sumTotalTokens_series to typeof(double), sumTotalTokens_moving_avg to typeof(double)
| project timestamp, sumTotalTokens_series, sumTotalTokens_moving_avg
| render timechart

